    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        Integer value1 = Integer.parseInt(JTheight.getText());
        Integer value2 = Integer.parseInt(JTwidth.getText());
        Integer value3 = Integer.parseInt(JTlength.getText());
        double volume = value1 * value2 * value3;

        String s=Double.toString(volume);    
        JLvolume.setText(s);  
    }//GEN-LAST:event_jButton2ActionPerformed

